const initialState = {
  currencies: [],
  expenses: [],
};

function wallet(state = initialState, action) {
  switch (action.type) {
  case 'GET_CURRENCY':
    return { ...state, currencies: action.payload };
  case 'FAILED_REQUEST':
    return { ...state, error: action.payload };
  default:
    return state;
  }
}

export default wallet;
