const initialState = {
  user: {
    email: '',
  },
};

const user = (state = initialState, action) => {
  switch (action.type) {
  case 'USER_INFO':
    return {
      ...state,
      user: {
        email: action.email,
      },
    };
  default:
    return state;
  }
};

export default user;
