import React from 'react';
import { Link } from 'react-router-dom';

class Login extends React.Component {
  constructor() {
    super();

    this.onChangeHandler = this.onChangeHandler.bind(this);

    this.state = {
      email: '',
      password: '',
    };
  }

  onChangeHandler(field, newValue) {
    this.setState({ [field]: newValue });
  }

  disableButton() {
    const re = /\S+@\S+\.\S+/;
    const minLength = 6;
    const { email, password } = this.state;
    return (
      email.length >= minLength && re.test(password)
        ? <Link to="/wallet"> Entrar </Link>
        : <span>Entrar</span>
    );
  }

  render() {
    return (
      <div>
        <input
          placeholder="Email"
          type="text"
          data-testid="email-input"
          onChange={ (e) => this.onChangeHandler('email', e.target.value) }
        />
        <input
          placeholder="Password"
          id="pas"
          type="text"
          data-testid="password-input"
          onChange={ (e) => this.onChangeHandler('password', e.target.value) }
        />
        { this.disableButton() }
      </div>
    );
  }
}

export default Login;
