import React, { Component } from 'react';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';
import { fetchCurrencies, saveExpense } from '../actions';
import fetchCurrency from '../services';
import '../style/header.css';

const INITIAL_STATE = {
  value: 0,
  description: '',
  currency: 'USD',
  method: 'Dinheiro',
  tag: 'Alimentação',
  id: 0,
  total: '0.00',
};

class AddForm extends Component {
  constructor() {
    super();

    this.state = {
      ...INITIAL_STATE,
    };

    this.onChangeHandler = this.onChangeHandler.bind(this);
    this.handleClick = this.handleClick.bind(this);
    this.totalExpenses = this.totalExpenses.bind(this);
  }

  componentDidMount() {
    const { getCurrency } = this.props;
    getCurrency();
  }

  onChangeHandler({ target }) {
    this.setState({
      [target.name]: target.value,
    });
  }

  totalExpenses() {
    const { expenses } = this.props;

    const totalExpenses = expenses.reduce(
      (total, payed) => {
        const { value, currency, exchangeRates } = payed;
        const rate = parseFloat(exchangeRates[currency]);
        console.log(total);
        return total + (parseFloat(value) * rate);
      },
      0,
    );
    return totalExpenses.toFixed(2);
  }

  async handleClick(event) {
    event.preventDefault();
    const { value, description, currency, method, tag, id } = this.state;
    const exchangeRates = await fetchCurrency();

    const expense = {
      value,
      description,
      currency,
      method,
      tag,
      id,
      exchangeRates,
    };

    saveExpense(expense);

    this.setState({
      ...INITIAL_STATE,
      id: id + 1,
      total: this.totalExpenses(),
    });
  }

  tagRender() {
    return (
      <div>
        <label htmlFor="tag">
          Categoria:
          <select
            id="tag"
            name="tag"
            onChange={ (e) => this.onChangeHandler(e) }
            data-testid="tag-input"
          >
            <option value="Alimentação">Alimentação</option>
            <option value="Lazer">Lazer</option>
            <option value="Trabalho">Trabalho</option>
            <option value="Transporte">Transporte</option>
            <option value="Saúde">Saúde</option>
          </select>
        </label>
      </div>
    );
  }

  methodRender() {
    return (
      <div>
        <label htmlFor="method">
          Método de pagamento:
          <select
            id="method"
            name="method"
            onChange={ (e) => this.onChangeHandler(e) }
            data-testid="method-input"
          >
            <option value="Dinheiro">Dinheiro</option>
            <option value="Cartão de crédito">Cartão de crédito</option>
            <option value="Cartão de débito">Cartão de débito</option>
          </select>
        </label>
      </div>
    );
  }

  currencyRender(currenciesList) {
    return (
      <select
        id="currency"
        name="currency"
        onChange={ (e) => this.onChangeHandler(e) }
        data-testid="currency-input"
      >
        { currenciesList.map((currency) => (
          currency === 'USDT' ? ''
            : <option key={ currency } data-testid={ currency }>{ currency }</option>
        )) }
      </select>
    );
  }

  header(user) {
    const { total } = this.state;
    return (
      <header>
        <h4>
          Email:
          <span data-testid="email-field">{ user }</span>
        </h4>
        <h4>
          Despesa total: R$
          <span data-testid="total-field">{ total }</span>
          <span data-testid="header-currency-field">BRL</span>
        </h4>
      </header>
    );
  }

  render() {
    const { currencies, user } = this.props;
    const currenciesTags = Object.keys(currencies || {});

    return (
      <>
        { this.header(user)}
        <div>
          <label htmlFor="value">
            Valor:
            <input
              id="value"
              name="value"
              type="number"
              onChange={ (e) => this.onChangeHandler(e) }
              data-testid="value-input"
            />
          </label>
        </div>
        <div>
          <label htmlFor="currency">
            Moeda:
            { this.currencyRender(currenciesTags) }
          </label>
        </div>
        { this.methodRender() }
        { this.tagRender() }
        <div>
          <label htmlFor="description">
            Descrição:
            <input
              id="description"
              name="description"
              type="text"
              onChange={ (e) => this.onChangeHandler(e) }
              data-testid="description-input"
            />
          </label>
          <button type="submit" onClick={ this.handleClick() }>Adicionar despesa</button>
        </div>
      </>
    );
  }
}

const mapStateToProps = (state) => ({
  expenses: state.wallet.expenses,
  currencies: state.wallet.currencies[0],
  user: state.user.email,
});

const mapDispatchToProps = (dispatch) => ({
  getCurrency: () => dispatch(fetchCurrencies()),
  saveExpense: (expense) => dispatch(saveExpense(expense)),
});

AddForm.propTypes = {
  expenses: PropTypes.arrayOf(PropTypes.object),
  currencies: PropTypes.objectOf(PropTypes.object),
  getCurrency: PropTypes.func.isRequired,
  saveExpense: PropTypes.func.isRequired,
  user: PropTypes.string.isRequired,
};

AddForm.defaultProps = {
  expenses: [],
  currencies: {},
};

export default connect(mapStateToProps, mapDispatchToProps)(AddForm);
