import React, { Component } from 'react';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';
import '../style/table.css';

class Table extends Component {
  expensesTable({ value, description, currency, method, tag, id, exchangeRates }) {
    return (
      <div key={ id } className="tableHeader">
        <p>{ description }</p>
        <p>{ tag }</p>
        <p>{ method }</p>
        <p>{ value }</p>
        <p>{ currency }</p>
        <p>Câmbio utilizad</p>
        <p>Valor convertid</p>
        <p>Moeda de conversã</p>
        <p>Editar/Exclui</p>
      </div>
    );
  }

  render() {
    const { expenses } = this.props;

    return (
      <div>
        <div className="tableHeader">
          <p>Descrição</p>
          <p>Tag</p>
          <p>Método de pagamento</p>
          <p>Valor</p>
          <p>Moeda</p>
          <p>Câmbio utilizado</p>
          <p>Valor convertido</p>
          <p>Moeda de conversão</p>
          <p>Editar/Excluir</p>
        </div>
        { expenses
          .map((expense) => (this.expensesTable(expense)))}
      </div>
    );
  }
}

const mapStateToProps = (state) => ({
  user: state.user.email,
  expenses: state.wallet.expenses,
});

Headers.propTypes = {
  user: PropTypes.string.isRequired,
  expenses: PropTypes.arrayOf(PropTypes.object),
};

Headers.defaultProps = {
  expenses: [],
};

export default connect(mapStateToProps)(Table);
